// src/components/books/FeaturedAuthors.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../services/axiosInstance";
import "../../styles/FeaturedAuthors.css";

const FeaturedAuthors = () => {
  const [authors, setAuthors] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    api.get("/books/featured-authors")
      .then(res => setAuthors(res.data || []));
  }, []);

  return (
    <div className="featured-authors">
      {authors.map((author, index) => (
        <div
          key={index}
          className="author-card"
          onClick={() =>
            navigate(`/authors/${encodeURIComponent(author.name)}`)
          }
        >
          <div className="author-avatar">{author.name.charAt(0)}</div>
          <h3>{author.name}</h3>
          <p>{author.book_count} books</p>
        </div>
      ))}
    </div>
  );
};

export default FeaturedAuthors;
