// frontend/src/components/books/BookDetails.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axiosInstance from "../../services/axiosInstance";
import RentModal from "../rentals/RentModal";

const BookDetails = () => {
  const { id } = useParams();

  const [book, setBook] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showRentModal, setShowRentModal] = useState(false);

  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [submitting, setSubmitting] = useState(false);

  const token = localStorage.getItem("token");

  // ----------------------------
  // Fetch Book Details
  // ----------------------------
  const fetchBook = async () => {
    try {
      const res = await axiosInstance.get(`/books/${id}`);
      setBook(res.data);
    } catch (err) {
      console.error("Failed to load book details", err);
    }
  };

  // ----------------------------
  // Fetch Reviews
  // ----------------------------
  const fetchReviews = async () => {
    try {
      const res = await axiosInstance.get(`/reviews/book/${id}`);
      setReviews(res.data.reviews || []);
    } catch (err) {
      console.error("Failed to load reviews", err);
    }
  };

  useEffect(() => {
    const loadData = async () => {
      await fetchBook();
      await fetchReviews();
      setLoading(false);
    };

    loadData();
  }, [id]);

  // ----------------------------
  // Submit Review
  // ----------------------------
  const handleSubmitReview = async (e) => {
    e.preventDefault();

    if (!token) {
      alert("You must be logged in to review.");
      return;
    }

    try {
      setSubmitting(true);

      await axiosInstance.post(
        `/reviews/book/${id}`,
        { rating, comment },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setComment("");
      setRating(5);

      await fetchReviews();
      await fetchBook();

    } catch (err) {
      alert(err.response?.data?.error || "Failed to submit review");
    } finally {
      setSubmitting(false);
    }
  };

  // ----------------------------
  // Render Stars
  // ----------------------------
  const renderStars = (value) => {
    return "⭐".repeat(value);
  };

  if (loading) return <p className="loading-text">Loading book details...</p>;
  if (!book) return <p className="error-text">Book not found</p>;

  return (
    <div className="book-details-container">
      <div className="book-details-card">
        <img
          src={book.image_url || "/book-placeholder.png"}
          alt={book.title}
          className="book-cover"
        />

        <div className="book-info">
          <h1>{book.title}</h1>

          <p><strong>Author:</strong> {book.author}</p>
          <p><strong>Genre:</strong> {book.genre}</p>

          <p className="book-description">
            {book.description || "No description available."}
          </p>

          <p><strong>Price per day:</strong> ₹{book.price_per_day}</p>

          <p><strong>Available Copies:</strong> {book.available_copies}</p>

          <p>
            <strong>Rating:</strong>{" "}
            {book.average_rating?.toFixed(1)} ({book.ratings_count} reviews)
          </p>

          <button
            className="btn-primary"
            disabled={book.available_copies <= 0}
            onClick={() => setShowRentModal(true)}
          >
            {book.available_copies > 0 ? "Rent Book" : "Out of Stock"}
          </button>
        </div>
      </div>

      {/* ---------------- Reviews Section ---------------- */}
      <div className="reviews-section">
        <h2>Reviews</h2>

        {reviews.length === 0 && <p>No reviews yet.</p>}

        {reviews.map((review) => (
          <div key={review.id} className="review-card">
            <p>{renderStars(review.rating)}</p>
            <p>{review.comment}</p>
            <small>
              {new Date(review.created_at).toLocaleDateString()}
            </small>
          </div>
        ))}

        {/* ---------------- Add Review Form ---------------- */}
        {token && (
          <form className="review-form" onSubmit={handleSubmitReview}>
            <h3>Add Review</h3>

            <label>Rating:</label>
            <select
              value={rating}
              onChange={(e) => setRating(Number(e.target.value))}
            >
              {[5,4,3,2,1].map((num) => (
                <option key={num} value={num}>
                  {num} Stars
                </option>
              ))}
            </select>

            <label>Comment:</label>
            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              required
            />

            <button type="submit" disabled={submitting}>
              {submitting ? "Submitting..." : "Submit Review"}
            </button>
          </form>
        )}
      </div>

      {showRentModal && (
        <RentModal
          book={book}
          onClose={() => setShowRentModal(false)}
        />
      )}
    </div>
  );
};

export default BookDetails;
