// src/pages/PaymentPage.jsx
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { rentBook } from "../services/rentalService";

const PaymentPage = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  // âœ… data passed from ConfirmRental page
  const { book, days } = state || {};

  const [loading, setLoading] = useState(false);

  // ðŸ”’ safety check
  if (!book || !days) {
    return (
      <div style={{ padding: "20px" }}>
        <h2>No payment data found</h2>
        <button onClick={() => navigate("/books")}>
          Go Back to Books
        </button>
      </div>
    );
  }

  const pricePerDay = 5;
  const totalAmount = days * pricePerDay;

  const confirmPayment = async () => {
    setLoading(true);
    try {
      // âœ… send duration also
      await rentBook(book.id, days);

      alert("Rental successful!");
      navigate("/my-rentals");
    } catch (err) {
      alert(err.response?.data?.error || "Rental failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="payment-container">
      <h1>Payment</h1>

      <div className="payment-card">
        <img
          src={book.image_url || "https://via.placeholder.com/150x220"}
          alt={book.title}
        />

        <div>
          <h2>{book.title}</h2>
          <p><strong>Author:</strong> {book.author}</p>
          <p><strong>Duration:</strong> {days} days</p>
          <p><strong>Price per day:</strong> â‚¹{pricePerDay}</p>
          <p><strong>Total Amount:</strong> â‚¹{totalAmount}</p>
        </div>
      </div>

      <button
        onClick={confirmPayment}
        disabled={loading}
        className="pay-btn"
      >
        {loading ? "Processing..." : "Confirm Payment"}
      </button>
    </div>
  );
};

export default PaymentPage;
