// src/pages/PaymentPage.jsx
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { rentBook } from "../services/rentalService";

const PaymentPage = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { book, duration } = state || {};
  const [loading, setLoading] = useState(false);

  if (!book) return <p>No book selected.</p>;

  const confirmPayment = async () => {
    setLoading(true);
    try {
      await rentBook(book.id);
      alert("Rental successful!");
      navigate("/my-rentals");
    } catch (err) {
      alert(err.response?.data?.error || "Rental failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="payment-container">
      <h1>Payment</h1>
      <h2>{book.title}</h2>
      <p>Duration: {duration} days</p>
      <p>Total: â‚¹{duration * 5}</p>

      <button onClick={confirmPayment} disabled={loading}>
        {loading ? "Processing..." : "Confirm Payment"}
      </button>
    </div>
  );
};

export default PaymentPage;
