// src/components/rentals/RentModal.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function RentModal({ book, onClose }) {
  const navigate = useNavigate();
  const [duration, setDuration] = useState(14);

  const handleProceed = () => {
    // Navigate to payment page with book & duration
    navigate("/payment", { state: { book, duration } });
  };

  return (
    <div className="modal-backdrop">
      <div className="modal-content">
        <h2>Rent "{book.title}"</h2>
        <p>Author: {book.author}</p>
        <p>Available Copies: {book.available_copies}</p>
        <p>Description: {book.description}</p>

        <label>
          Duration (days):
          <input
            type="number"
            min="1"
            max="30"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
        </label>

        <div className="modal-buttons">
          <button onClick={onClose}>Cancel</button>
          <button onClick={handleProceed}>Proceed to Payment</button>
        </div>
      </div>
    </div>
  );
}

export default RentModal;
