import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { logout } from "../../services/authService";
import "../../styles/Navbar.css"

const Navbar = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/home" className="logo">ðŸ“š Library</Link>
      </div>

      <div className="navbar-right">
        {/* Updated to /home */}
        <Link to="/home">Home</Link>
        <Link to="/catalog">Catalog</Link>
        <Link to="/about">About</Link>

        {user ? (
          <>
            {/* NEW: Admin specific links */}
            {user.role === "admin" && (
              <>
                <Link to="/admin/dashboard" className="admin-link">Dashboard</Link>
                <Link to="/admin/users" className="admin-link">Users</Link>
              </>
            )}

            <Link to="/profile">Welcome, {user.username}</Link> 
            <Link to="/my-rentals" className="btn-my-rentals">My Rentals</Link>
            <button onClick={handleLogout} className="btn-logout">Logout</button>
          </>
        ) : (
          <Link to="/login" className="btn-login">Login</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
