import React, { useState, useEffect } from "react";
import axiosInstance from "../../services/axiosInstance";
import "../../styles/AdminBooks.css";

const AdminBooks = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchBooks = async () => {
    try {
      const res = await axiosInstance.get("/admin/books");
      setBooks(res.data);
      setLoading(false);
    } catch (err) {
      console.error("Failed to load books database");
    }
  };

  useEffect(() => { fetchBooks(); }, []);

  const handleDelete = async (id) => {
    if (window.confirm("Confirm deletion of this book from database?")) {
      await axiosInstance.delete(`/admin/books/${id}`);
      fetchBooks();
    }
  };

  const handlePriceChange = async (id, newPrice) => {
    await axiosInstance.put(`/admin/books/${id}`, { price: newPrice });
    fetchBooks();
  };

  return (
    <div className="admin-management-page">
      <div className="management-header">
        <h2>Books Inventory Management</h2>
        <button className="btn-add-book">+ Add New Book</button>
      </div>

      <table className="admin-db-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Author</th>
            <th>Current Cost</th>
            <th>Stock</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.id}>
              <td>#{book.id}</td>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>
                <input 
                  type="number" 
                  defaultValue={book.price} 
                  onBlur={(e) => handlePriceChange(book.id, e.target.value)}
                  className="price-input"
                />
              </td>
              <td>{book.stock}</td>
              <td>
                <button className="btn-update">Update Info</button>
                <button className="btn-delete" onClick={() => handleDelete(book.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminBooks;
