import React, { useState } from "react";
import RentModal from "../rentals/RentModal"; // Import the RentModal component

const BookCard = ({ book }) => {
  const [isRentOpen, setIsRentOpen] = useState(false);

  return (
    <div className="book-card">
      <img
        src={book.image_url || "https://via.placeholder.com/200x280"}
        alt={book.title}
        className="book-image"
      />

      <div className="book-content">
        <h3 className="book-title">{book.title}</h3>
        <p className="book-author">by {book.author}</p>

        <span className="book-genre">{book.genre || "General"}</span>

        <p
          className={`book-availability ${
            book.available_copies > 0 ? "available" : "unavailable"
          }`}
        >
          {book.available_copies > 0
            ? `Available (${book.available_copies})`
            : "Not Available"}
        </p>

        <button
          className="btn-primary"
          disabled={book.available_copies <= 0}
          onClick={() => setIsRentOpen(true)}
        >
          Rent Book
        </button>
      </div>

      {/* Rent Modal */}
      {isRentOpen && (
        <RentModal
          book={book}
          onClose={() => setIsRentOpen(false)}
        />
      )}
    </div>
  );
};

export default BookCard;
