import React, { useState } from "react";
import RentModal from "../rentals/RentModal";
import { rentBook } from "../../services/rentalService";

const BookCard = ({ book }) => {
  const [showModal, setShowModal] = useState(false);

  const handleRent = async () => {
    try {
      await rentBook(book.id);
      alert("Book rented successfully!");
      setShowModal(false);
    } catch (err) {
      alert(`Failed to rent book: ${err.message}`);
    }
  };

  return (
    <div className="book-card">
      <h3>{book.title}</h3>
      <p>by {book.author}</p>
      <p>{book.available_copies > 0 ? "Available" : "Not available"}</p>
      <button
        disabled={book.available_copies <= 0}
        onClick={() => setShowModal(true)}
      >
        Rent Book
      </button>

      {showModal && (
        <RentModal
          book={book}
          onClose={() => setShowModal(false)}
          onConfirm={handleRent}
        />
      )}
    </div>
  );
};

export default BookCard;
