// src/services/rentalService.js
import axiosInstance from "./axiosInstance";

export const rentBook = async (bookId, duration = 14) => {
  try {
    const response = await axiosInstance.post("/rentals", {
      book_id: bookId,
      duration, // optional, your backend can ignore
    });
    return response.data;
  } catch (err) {
    const message =
      err.response?.data?.error || err.message || "Failed to rent book";
    throw new Error(message);
  }
};

export const getUserRentals = async () => {
  try {
    const response = await axiosInstance.get("/rentals/user");
    return response.data;
  } catch (err) {
    const message =
      err.response?.data?.error || err.message || "Failed to fetch rentals";
    throw new Error(message);
  }
};
