import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "../../services/axiosInstance";
import "../../styles/Categories.css";

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axiosInstance
      .get("/books/categories")
      .then((res) => setCategories(res.data || []))
      .catch((err) => console.error(err));
  }, []);

  const handleClick = (category) => {
    navigate(`/books?category=${encodeURIComponent(category)}`);
  };

  return (
    <div className="container">

      <div className="category-grid">
        {categories.map((cat, index) => {
          // Convert category title to lowercase, remove spaces to match CSS classes
          const catClass = cat.title.toLowerCase().replace(/\s/g, "");
          return (
            <div
              key={index}
              className={`category-card ${catClass}`}
              onClick={() => handleClick(cat.title)}
            >
              <h3>{cat.title}</h3>
              <p>{cat.items} books</p>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Categories;
