//frontend\src\components\books\BookCard.js
import React, { useState } from "react";
import RentModal from "../rentals/RentModal";

const BookCard = ({ book }) => {
  const [openModal, setOpenModal] = useState(false);

  return (
    <>
      <div
        className="book-card"
        style={{ cursor: "pointer" }}
        onClick={() => setOpenModal(true)}   // ✅ open modal on click
      >
        <img
          src={book.image_url || "https://via.placeholder.com/200x280"}
          alt={book.title}
        />

        <div className="book-info">
          <h3>{book.title}</h3>
          <p>{book.author}</p>
          <p>
            {book.available_copies > 0
              ? `Available (${book.available_copies})`
              : "Not Available"}
          </p>
        </div>
      </div>

      {/* ✅ Rent modal stays on SAME PAGE */}
      {openModal && (
        <RentModal
          book={book}
          onClose={() => setOpenModal(false)}
        />
      )}
    </>
  );
};

export default BookCard;
