// src/components/books/Categories.js
import React from "react";

const categories = [
  { title: "Books", items: 2450 },
  { title: "Audiobooks", items: 1230 },
  { title: "Magazines", items: 890 },
  { title: "Comics", items: 1560 },
  { title: "Study Materials", items: 780 },
];

const Categories = () => {
  return (
    <div className="container">
      <h2 className="section-title">Browse by Category</h2>
      <div className="category-grid">
        {categories.map((cat, index) => (
          <div key={index} className="category-card">
            <div className="category-icon"></div>
            <h3>{cat.title}</h3>
            <p>{cat.items} items</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Categories;
