import React, { useEffect, useState } from "react";
import { getBooks } from "../../services/bookService";

const Catalog = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBooks = async () => {
      const data = await getBooks();
      setBooks(data);
      setLoading(false);
    };
    fetchBooks();
  }, []);

  if (loading) {
    return <p style={{textAlign: "center"}}>Loading books...</p>;
  }

  return (
    <div className="container">
      <h2 className="section-title">Book Catalog</h2>
      <div className="media-grid">
        {books.length === 0 ? (
          <p>No books found.</p>
        ) : (
          books.map((book) => (
            <div className="media-card" key={book.id}>
              <div
                className="media-cover"
                style={{
                  background: book.image_url
                    ? `url(${book.image_url}) center/cover no-repeat`
                    : "#ddd",
                  fontSize: book.image_url ? 0 : "5rem",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                }}
              >
                {!book.image_url && "üìö"}
              </div>
              <div className="media-info">
                <span className="media-type">Book</span>
                <h3>{book.title}</h3>
                <p className="author">by {book.author}</p>
                <div className="media-footer">
                  <span className="price">$2.99</span>
                  <span className="duration">
                    ‚è±Ô∏è {book.available_copies}/{book.total_copies} available
                  </span>
                </div>
                <button
                  className={`rent-btn ${
                    book.available_copies === 0 ? "unavailable" : ""
                  }`}
                >
                  {book.available_copies === 0 ? "Unavailable" : "Rent Now"}
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Catalog;
