// src/pages/PaymentPage.jsx
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { rentBook } from "../services/rentalService";

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { book, duration } = location.state || {};
  const [loading, setLoading] = useState(false);

  if (!book) return <p>No book selected.</p>;

  const handleConfirmPayment = async () => {
    setLoading(true);
    try {
      // Call backend to create rental
      await rentBook(book.id);
      alert(`Rental confirmed for "${book.title}" for ${duration} days.`);
      navigate("/my-rentals");
    } catch (err) {
      console.error("Failed to rent book:", err.response?.data || err.message);
      alert("Failed to rent book. Make sure you are logged in.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Payment Page</h1>
      <h2>{book.title}</h2>
      <p>Author: {book.author}</p>
      <p>Duration: {duration} days</p>
      <p>Price: â‚¹{duration * 5} (dummy)</p>
      <button onClick={handleConfirmPayment} disabled={loading}>
        {loading ? "Processing..." : "Confirm Payment"}
      </button>
    </div>
  );
};

export default PaymentPage;
