import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { rentBook } from "../services/rentalService";
import "../styles/Payment.css";

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  // Use 'days' to match ConfirmRental.js
  const book = location.state?.book;
  const days = location.state?.days; 

  if (!book || !days) {
    return (
      <div className="payment-container">
        <div className="error-card">
          <h2>No payment details found</h2>
          <p>Please select a book and duration from the catalog.</p>
          <button onClick={() => navigate("/catalog")} className="pay-btn">
            Back to Catalog
          </button>
        </div>
      </div>
    );
  }

  // Use rental_price_per_day from your JSON data
  const pricePerDay = book.rental_price_per_day || 10; 
  const totalAmount = pricePerDay * days;

  const confirmPayment = async () => {
    setLoading(true);
    try {
      // Pass both book.id and days to your service
      await rentBook(book.id, days); 
      setSuccess(true);
    } catch (err) {
      alert(err.response?.data?.error || "Rental failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="payment-container">
      {!success ? (
        <>
          <h1>ðŸ’³ Payment Confirmation</h1>
          <div className="payment-card">
            <img 
              src={book.image_url || "https://via.placeholder.com/160x240"} 
              alt={book.title} 
            />
            <div className="payment-info">
              <h2>{book.title}</h2>
              <p><strong>Author:</strong> {book.author}</p>
              <hr />
              <p><strong>Price per day:</strong> â‚¹{pricePerDay}</p>
              <p><strong>Duration:</strong> {days} days</p>
              <p className="total-amount">
                <strong>Total Amount:</strong> â‚¹{totalAmount}
              </p>
            </div>
          </div>

          <button className="pay-btn" disabled={loading} onClick={confirmPayment}>
            {loading ? "Processing..." : `Confirm & Pay â‚¹${totalAmount}`}
          </button>
        </>
      ) : (
        <div className="success-card">
          <h1>âœ… Rental Successful</h1>
          <p>Enjoy reading <strong>{book.title}</strong>!</p>
          <button className="pay-btn" onClick={() => navigate("/my-rentals")}>
            Go to My Rentals
          </button>
        </div>
      )}
    </div>
  );
};

export default PaymentPage;
