import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/axiosInstance";
import "../styles/FeaturedAuthors.css";

const FeaturedAuthors = () => {
  const [authors, setAuthors] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    loadAuthors();
  }, []);

  const loadAuthors = async () => {
    try {
      const res = await api.get("/books/featured-authors");
      setAuthors(res.data || []);
    } catch (err) {
      console.error("Failed to load authors", err);
    }
  };

  const handleAuthorClick = (authorName) => {
    navigate(`/authors/${encodeURIComponent(authorName)}`);
  };

  return (
    <div className="featured-authors-page">
      <h2 className="section-title">✍️ Featured Authors</h2>

      <div className="featured-authors">
        {authors.map((author, index) => (
          <div
            key={index}
            className="author-card"
            onClick={() => handleAuthorClick(author.name)}
            role="button"
            tabIndex={0}
          >
            <div className="author-avatar">
              {author.name.charAt(0)}
            </div>

            <h3>{author.name}</h3>
            <p>{author.book_count} books</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FeaturedAuthors;
