import React, { useState, useEffect } from "react";
import adminService from "../../services/adminService";
import "../../styles/AdminBooks.css";

const AdminBooks = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  // 1. Fetch all books - Adjusted for paginated response
  const fetchBooks = async () => {
    try {
      const data = await adminService.getAllBooks();
      // data.books contains the array from your paginated backend
      setBooks(data.books || []); 
      setLoading(false);
    } catch (err) {
      console.error("Failed to load books database", err);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  // 2. Delete a book
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this book?")) return;
    try {
      await adminService.deleteBook(id);
      setBooks(prevBooks => prevBooks.filter(b => b.id !== id));
      alert("Book deleted successfully!");
    } catch (err) {
      console.error("Delete error:", err);
      alert(err.response?.data?.error || "Error deleting book");
    }
  };

  // 3. Handle Local Price Input (UI only)
  const handleLocalPriceUpdate = (id, value) => {
    setBooks(prevBooks =>
      prevBooks.map(b => (b.id === id ? { ...b, price: value } : b))
    );
  };

  // 4. Save Price Update to Database
  const handleSavePrice = async (id, newPrice) => {
    try {
      await adminService.updateBook(id, { price: parseFloat(newPrice) });
      alert("Price updated successfully!");
    } catch (err) {
      console.error("Update error:", err);
      alert("Failed to update price on server.");
    }
  };

  if (loading) return <p className="loading-text">Loading books...</p>;

  return (
    <div className="admin-books-container">
      <div className="books-header">
        <h1>Books Inventory Management</h1>
        <button className="btn-add-book" onClick={() => alert("Open Add Modal")}>
          + Add New Book
        </button>
      </div>

      <div className="books-table-wrapper">
        <table className="admin-db-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Author</th>
              <th>Price ($)</th>
              <th>Stock</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {books.length > 0 ? (
              books.map(book => (
                <tr key={book.id}>
                  <td>#{book.id}</td>
                  <td>{book.title}</td>
                  <td>{book.author}</td>
                  <td>
                    <input
                      type="number"
                      value={book.price}
                      onChange={e => handleLocalPriceUpdate(book.id, e.target.value)}
                      onBlur={e => handleSavePrice(book.id, e.target.value)} // Saves when clicking away
                      className="price-input"
                    />
                  </td>
                  <td>{book.total_copies || book.stock}</td> {/* Matches backend field */}
                  <td>
                    <button 
                      className="btn-update" 
                      onClick={() => handleSavePrice(book.id, book.price)}
                    >
                      Update
                    </button>
                    <button
                      className="btn-delete"
                      onClick={() => handleDelete(book.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" style={{ textAlign: "center", padding: "20px" }}>
                  No books found in the database.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminBooks;
