import React, { useEffect, useState } from "react";
import { useSearchParams, Link } from "react-router-dom";
import axiosInstance from "../../services/axiosInstance";

const CategoryBooks = () => {
  const [searchParams] = useSearchParams();
  const category = searchParams.get("category");

  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!category) return;

    const fetchBooks = async () => {
      try {
        const res = await axiosInstance.get(
          `/books?category=${encodeURIComponent(category)}`
        );
        setBooks(res.data.books);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, [category]);

  if (!category) {
    return <h2 style={{ textAlign: "center" }}>No category selected</h2>;
  }

  if (loading) {
    return <h2 style={{ textAlign: "center" }}>Loading books...</h2>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2>Category: {category}</h2>

      {books.length === 0 ? (
        <p>No books found in this category.</p>
      ) : (
        <div className="books-grid">
          {books.map((book) => (
            <div className="book-card" key={book.id}>
              <img
                src={book.image_url}
                alt={book.title}
                className="book-image"
              />

              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <p>Available ({book.available_copies})</p>

              <Link to={`/books/${book.id}`} className="btn">
                Rent Book
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CategoryBooks;
