import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login } from "../services/authService";
import "../styles/Login.css";

const Login = () => {
  const navigate = useNavigate();
  
  // State for form fields
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  
  // NEW: State to track if the user is trying to log in as Admin or User
  const [isAdminLogin, setIsAdminLogin] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const result = await login(username, password);

      if (result.error) {
        setError(result.error);
      } else {
        // Validation: Ensure the person logging in matches the role they selected
        if (isAdminLogin && result.user.role !== "admin") {
          setError("Access Denied: You do not have Admin privileges.");
          setLoading(false);
          return;
        }

        // Save session data
        localStorage.setItem("access_token", result.access_token);
        localStorage.setItem("user", JSON.stringify(result.user));

        // Route based on role
        if (result.user.role === "admin") {
          navigate("/admin/dashboard");
        } else {
          navigate("/home");
        }
      }
    } catch (err) {
      setError("Server connection failed.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      <div className="login-card">
        {/* Role Switcher Tabs */}
        <div className="login-tabs">
          <button 
            className={!isAdminLogin ? "active-tab" : ""} 
            onClick={() => { setIsAdminLogin(false); setError(""); }}
          >
            User Login
          </button>
          <button 
            className={isAdminLogin ? "active-tab admin-active" : ""} 
            onClick={() => { setIsAdminLogin(true); setError(""); }}
          >
            Admin Login
          </button>
        </div>

        <h2 className="login-title">
          {isAdminLogin ? "Admin Portal" : "Member Login"}
        </h2>
        
        {error && <div className="login-error">{error}</div>}
        
        <form onSubmit={handleSubmit} className="login-form">
          <label>Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder={isAdminLogin ? "Admin Username" : "User Username"}
            required
          />

          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="••••••••"
            required
          />

          <button 
            type="submit" 
            className={`login-button ${isAdminLogin ? "admin-btn" : ""}`} 
            disabled={loading}
          >
            {loading ? "Verifying..." : isAdminLogin ? "Login as Admin" : "Login"}
          </button>
        </form>

        {!isAdminLogin && (
          <p className="login-footer">
            New here? <a href="/register">Create an account</a>
          </p>
        )}
      </div>
    </div>
  );
};

export default Login;
