import { useLocation, useNavigate } from "react-router-dom";
import { rentBook } from "../services/rentalService";

function PaymentPage() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { book, duration } = state;

  const handlePayment = async () => {
    try {
      await rentBook(book.id); // Call backend
      alert("Book rented successfully!");
      navigate("/my-rentals");
    } catch (error) {
      console.error(error);
      alert("Failed to rent book: " + error.error || error.message);
    }
  };

  return (
    <div>
      <h2>Payment for {book.title}</h2>
      <p>Author: {book.author}</p>
      <p>Duration: {duration} days</p>
      <p>Cost: â‚¹{duration * 5}</p>
      <button onClick={handlePayment}>Pay & Rent</button>
    </div>
  );
}

export default PaymentPage;
