import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../services/axiosInstance";
import "../../styles/FeaturedAuthors.css";

const FeaturedAuthors = () => {
  const [authors, setAuthors] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAuthors = async () => {
      try {
        const res = await api.get("/books/featured-authors");
        setAuthors(res.data || []);
      } catch (err) {
        console.error(err);
      } finally { setLoading(false); }
    };
    fetchAuthors();
  }, []);

  if (loading) return <p className="container">Loading authors...</p>;

  return (
    /* Wrap in 'container' to fix the stretching issue */
    <div className="container">
      <h2 className="section-title">Featured Authors</h2>
      <div className="featured-authors">
        {authors.map((author) => (
          <div key={author.name} className="author-card" onClick={() => navigate(`/authors/${encodeURIComponent(author.name)}`)}>
            <div className="author-avatar">{author.name?.charAt(0).toUpperCase()}</div>
            <h3 className="author-name">{author.name}</h3>
            <p className="author-books">{author.book_count} books</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FeaturedAuthors;
