// src/pages/PaymentPage.jsx
import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const { book, duration } = location.state || {};

  if (!book) {
    return <p>No book selected for payment.</p>;
  }

  const handleDummyPayment = () => {
    // Dummy payment: just alert & navigate back to catalog or MyRentals
    alert(
      `Payment completed for "${book.title}" for ${duration} days.\nRental is now confirmed (dummy).`
    );

    // Navigate to My Rentals page
    navigate("/my-rentals");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Payment Page</h1>
      <h2>{book.title}</h2>
      <p>Author: {book.author}</p>
      <p>Duration: {duration} days</p>
      <p>Price: â‚¹{duration * 5} (dummy)</p>

      <button onClick={handleDummyPayment}>Confirm Payment</button>
    </div>
  );
};

export default PaymentPage;
