// src/components/books/Catalog.js
import React from "react";
import BookCard from "./BookCard";

const catalogBooks = [
  { type: "Book", title: "Project Hail Mary", author: "Andy Weir", price: 2.99, duration: "7 days", rating: 4.6 },
  { type: "Audiobook", title: "Educated", author: "Tara Westover", price: 3.99, duration: "14 days", rating: 4.7, unavailable: true },
  { type: "Magazine", title: "Wired Magazine", author: "Cond√© Nast", price: 1.99, duration: "30 days", rating: 4.4 },
  { type: "Study Material", title: "Python Crash Course", author: "Eric Matthes", price: 4.99, duration: "30 days", rating: 4.9 },
  { type: "Book", title: "The Silent Patient", author: "Alex Michaelides", price: 2.99, duration: "7 days", rating: 4.5 },
  { type: "Comic", title: "Batman: Year One", author: "DC Comics", price: 1.49, duration: "7 days", rating: 4.8 },
];

const Catalog = () => {
  return (
    <div id="catalog" className="container">
      <h2 className="section-title">Complete Catalog</h2>
      <div className="search-section">
        <div className="search-bar">
          <span className="search-icon">üîç</span>
          <input type="text" placeholder="Search by title or author..." />
        </div>
      </div>

      <div className="filters">
        {["All","Books","Audiobooks","Magazines","Comics","Study Materials"].map((f, i) => (
          <button key={i} className={`filter-btn ${i===0 ? "active" : ""}`}>{f}</button>
        ))}
      </div>

      <div className="media-grid">
        {catalogBooks.map((book, i) => (
          <BookCard key={i} book={book} />
        ))}
      </div>
    </div>
  );
};

export default Catalog;
