//frontend\src\components\books\CategoryBooks.js
// frontend\src\components\books\CategoryBooks.js
import React, { useEffect, useState } from "react";
import axiosInstance from "../../services/axiosInstance";
import BookCard from "./BookCard";
import BookDetailsModal from "./BookDetailsModal";
import "../../styles/CategoryBooks.css";

const CategoryBooks = ({ category }) => {   // Optional: category prop
  const [books, setBooks] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchBooks = async (search = "") => {
    try {
      setLoading(true);
      const params = { search };
      if (category) params.category = category;

      const res = await axiosInstance.get("/books", { params });
      setBooks(res.data.books || []);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, [category]);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    fetchBooks(value);  // live search
  };

  return (
    <div className="category-books-container">
      {/* ---------- Search Bar ---------- */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search books..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </div>

      {/* ---------- Books Grid ---------- */}
      <div className="books-grid">
        {loading ? (
          <p>Loading books...</p>
        ) : books.length === 0 ? (
          <p>No books found.</p>
        ) : (
          books.map((book) => (
            <BookCard
              key={book.id}
              book={book}
              onSelect={setSelectedBook}
            />
          ))
        )}
      </div>

      {/* ---------- Book Details Modal ---------- */}
      {selectedBook && (
        <BookDetailsModal
          bookId={selectedBook.id}
          onClose={() => setSelectedBook(null)}
        />
      )}
    </div>
  );
};

export default CategoryBooks;
