import React, { useEffect, useState } from "react";
import axiosInstance from "../../services/axiosInstance";
import BookCard from "./BookCard";
import BookDetailsModal from "./BookDetailsModal";
import "../../styles/CategoryBooks.css";

const CategoryBooks = ({ category }) => {
  const [books, setBooks] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const fetchBooks = async (search = "", pageNumber = 1) => {
    try {
      setLoading(true);
      const params = { search, page: pageNumber, per_page: 6 }; // 6 books per page
      if (category) params.category = category;

      const res = await axiosInstance.get("/books", { params });
      setBooks(res.data.books || []);
      setTotalPages(res.data.pages || 1);
      setPage(res.data.current_page || 1);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks(searchTerm, page);
  }, [category]);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    fetchBooks(value, 1); // reset to page 1 on search
  };

  const handlePageChange = (newPage) => {
    fetchBooks(searchTerm, newPage);
  };

  return (
    <div className="category-books-container">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search books..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </div>

      <div className="books-grid">
        {loading ? (
          <p>Loading books...</p>
        ) : books.length === 0 ? (
          <p>No books found.</p>
        ) : (
          books.map((book) => (
            <BookCard
              key={book.id}
              book={book}
              onSelect={setSelectedBook}
            />
          ))
        )}
      </div>

      {/* ---------- Pagination ---------- */}
      <div className="pagination">
        <button
          onClick={() => handlePageChange(page - 1)}
          disabled={page <= 1}
        >
          Prev
        </button>
        <span>
          Page {page} of {totalPages}
        </span>
        <button
          onClick={() => handlePageChange(page + 1)}
          disabled={page >= totalPages}
        >
          Next
        </button>
      </div>

      {selectedBook && (
        <BookDetailsModal
          bookId={selectedBook.id}
          onClose={() => setSelectedBook(null)}
        />
      )}
    </div>
  );
};

export default CategoryBooks;
