// src/components/rentals/RentModal.jsx
import React, { useState, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/RentModal.css";

const RentModal = ({ book, onClose }) => {
  const navigate = useNavigate();

  // Default rental duration (auto-return policy)
  const [duration, setDuration] = useState(14);

  // Calculate total price automatically
  const totalPrice = useMemo(() => {
    if (!book?.price_per_day) return 0;
    return duration * book.price_per_day;
  }, [duration, book]);

  const proceedToPayment = () => {
    navigate("/payment", {
      state: {
        bookId: book.id,
        title: book.title,
        pricePerDay: book.price_per_day,
        duration,
        totalPrice,
      },
    });
  };

  return (
    <div className="modal-backdrop">
      <div className="modal-content">
        <h2>ðŸ“š Confirm Rental</h2>

        <div className="rental-summary">
          <p>
            <strong>Book:</strong> {book.title}
          </p>
          <p>
            <strong>Author:</strong> {book.author}
          </p>
          <p>
            <strong>Price / Day:</strong> â‚¹{book.price_per_day}
          </p>
          <p>
            <strong>Available Copies:</strong> {book.available_copies}
          </p>
        </div>

        <label className="duration-label">
          Rental Duration (days)
          <input
            type="number"
            min="1"
            max="30"
            value={duration}
            onChange={(e) => setDuration(Number(e.target.value))}
          />
        </label>

        <div className="price-box">
          <p>
            <strong>Total Amount:</strong> â‚¹{totalPrice}
          </p>
          <small className="policy-note">
            ðŸ”„ Book will be auto-returned after {duration} days.  
            No late fine is applied.
          </small>
        </div>

        <div className="modal-buttons">
          <button className="btn-secondary" onClick={onClose}>
            Cancel
          </button>
          <button
            className="btn-primary"
            disabled={book.available_copies <= 0}
            onClick={proceedToPayment}
          >
            Proceed to Payment
          </button>
        </div>
      </div>
    </div>
  );
};

export default RentModal;
