import React, { useState, useEffect } from "react";
import adminService from "../../services/adminService";
import "../../styles/AdminBooks.css";

const AdminBooks = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch all books
  const fetchBooks = async () => {
    try {
      const data = await adminService.getAllBooks();
      setBooks(data);
      setLoading(false);
    } catch (err) {
      console.error("Failed to load books database", err);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  // Delete a book
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this book?")) return;
    try {
      await adminService.deleteBook(id);
      setBooks(prevBooks => prevBooks.filter(b => b.id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  // Update book price
  const handlePriceChange = async (id, newPrice) => {
    try {
      await adminService.updateBook(id, { price: newPrice });
      setBooks(prevBooks =>
        prevBooks.map(b => (b.id === id ? { ...b, price: newPrice } : b))
      );
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <p style={{ textAlign: "center" }}>Loading books...</p>;

  return (
    <div className="admin-books-container">
      <div className="books-header">
        <h1>Books Inventory Management</h1>
        <button className="btn-add-book">+ Add New Book</button>
      </div>

      <table className="books-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Author</th>
            <th>Price ($)</th>
            <th>Stock</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map(book => (
            <tr key={book.id}>
              <td>#{book.id}</td>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>
                <input
                  type="number"
                  value={book.price}
                  onChange={e => handlePriceChange(book.id, e.target.value)}
                  className="price-input"
                />
              </td>
              <td>{book.stock}</td>
              <td>
                <button className="btn-update">Update</button>
                <button
                  className="btn-delete"
                  onClick={() => handleDelete(book.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminBooks;
