import React, { useState, useEffect } from "react";
import { getUserProfile, updateUserProfile, changePassword } from "../services/authService";
import "../styles/UserProfilePage.css";

const UserProfilePage = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({});
  const [passwordData, setPasswordData] = useState({ oldPassword: "", newPassword: "" });

  useEffect(() => {
    async function fetchProfile() {
      try {
        const data = await getUserProfile();
        setUser(data);
        setFormData({ username: data.username, email: data.email, full_name: data.full_name || "", phone: data.phone || "", address: data.address || "" });
      } catch (err) {
        console.error(err);
        alert("Failed to fetch profile");
      } finally {
        setLoading(false);
      }
    }
    fetchProfile();
  }, []);

  if (loading) return <p>Loading profile...</p>;
  if (!user) return <p>No user found</p>;

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handlePasswordChange = (e) => {
    setPasswordData({ ...passwordData, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      const updated = await updateUserProfile(formData);
      setUser(updated);
      alert("Profile updated successfully!");
      setEditMode(false);
    } catch (err) {
      console.error(err);
      alert("Failed to update profile");
    }
  };

  const handleChangePassword = async () => {
    try {
      await changePassword(passwordData);
      alert("Password changed successfully!");
      setPasswordData({ oldPassword: "", newPassword: "" });
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Failed to change password");
    }
  };

  return (
    <div className="profile-page">
      <h1>User Profile</h1>

      <div className="profile-card">
        <h2>Profile Details</h2>
        {editMode ? (
          <>
            <label>Username:</label>
            <input type="text" name="username" value={formData.username} onChange={handleInputChange} />
            <label>Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleInputChange} />
            <label>Full Name:</label>
            <input type="text" name="full_name" value={formData.full_name} onChange={handleInputChange} />
            <label>Phone:</label>
            <input type="text" name="phone" value={formData.phone} onChange={handleInputChange} />
            <label>Address:</label>
            <input type="text" name="address" value={formData.address} onChange={handleInputChange} />
            <button onClick={handleSave}>Save</button>
            <button onClick={() => setEditMode(false)}>Cancel</button>
          </>
        ) : (
          <>
            <p><strong>Username:</strong> {user.username}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Full Name:</strong> {user.full_name || "N/A"}</p>
            <p><strong>Phone:</strong> {user.phone || "N/A"}</p>
            <p><strong>Address:</strong> {user.address || "N/A"}</p>
            <button onClick={() => setEditMode(true)}>Edit Profile</button>
          </>
        )}
      </div>

      <div className="profile-card">
        <h2>Change Password</h2>
        <label>Old Password:</label>
        <input type="password" name="oldPassword" value={passwordData.oldPassword} onChange={handlePasswordChange} />
        <label>New Password:</label>
        <input type="password" name="newPassword" value={passwordData.newPassword} onChange={handlePasswordChange} />
        <button onClick={handleChangePassword}>Change Password</button>
      </div>
    </div>
  );
};

export default UserProfilePage;
