// src/components/common/Hero.js
import React from "react";
import "../../styles/Hero.css";

const Hero = () => {
  return (
    <section id="home" className="hero">
      <h1>The Gateway to Infinite Worlds</h1>
      <p>Browse thousands of books, audiobooks, magazines, comics, and study materials</p>
      <a href="#catalog" className="cta-button">Explore Library</a>
    </section>
  );
};

export default Hero;
