// src/components/rentals/RentalCard.jsx
import React from "react";
import "./RentalCard.css"; // optional: create CSS file for styling

function formatDate(dateStr) {
  if (!dateStr) return "N/A";
  const date = new Date(dateStr);
  return date.toLocaleDateString(); // Format like 31/01/2026
}

function RentalCard({ rental }) {
  return (
    <div className={`rental-card ${rental.status}`}>
      <h3 className="rental-title">{rental.book.title}</h3>
      <p className="rental-author"><strong>Author:</strong> {rental.book.author}</p>
      <p className="rental-dates">
        <strong>Rented On:</strong> {formatDate(rental.rental_date)} |{" "}
        <strong>Due:</strong> {formatDate(rental.due_date)}
      </p>
      <p className="rental-return">
        <strong>Returned On:</strong> {rental.return_date ? formatDate(rental.return_date) : "Not returned"}
      </p>
      <p className={`rental-status ${rental.status}`}>
        <strong>Status:</strong> {rental.status.charAt(0).toUpperCase() + rental.status.slice(1)}
      </p>
      <p className="rental-fine"><strong>Fine:</strong> â‚¹{rental.fine_amount.toFixed(2)}</p>
    </div>
  );
}

export default RentalCard;
