import axiosInstance from "./axiosInstance";

export const rentBook = async (bookId) => {
  try {
    const response = await axiosInstance.post("/rentals", { book_id: bookId });
    return response.data;
  } catch (err) {
    // This is the proper way to get backend error
    const message =
      err.response?.data?.error || err.message || "Failed to rent book";
    throw new Error(message);
  }
};

export const getUserRentals = async () => {
  try {
    const response = await axiosInstance.get("/rentals/user");
    return response.data;
  } catch (err) {
    const message =
      err.response?.data?.error || err.message || "Failed to fetch rentals";
    throw new Error(message);
  }
};

// Return a book
export const returnBook = async (rentalId) => {
  try {
    const response = await axiosInstance.put(`/rentals/${rentalId}/return`);
    return response.data;
  } catch (error) {
    throw error.response?.data || error;
  }
};
