//frontend\src\components\books\CategoryBooks.js
import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import axiosInstance from "../../services/axiosInstance";
import BookDetailsModal from "./BookDetailsModal";

const CategoryBooks = () => {
  const [searchParams] = useSearchParams();
  const category = searchParams.get("category");

  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  const [selectedBook, setSelectedBook] = useState(null);

  useEffect(() => {
    if (!category) return;

    axiosInstance
      .get(`/books?category=${encodeURIComponent(category)}`)
      .then((res) => setBooks(res.data.books || []))
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [category]);

  if (!category) return <h2>No category selected</h2>;
  if (loading) return <h2>Loading books...</h2>;

  return (
    <div className="container">
      <h2>Category: {category}</h2>

      <div className="books-grid">
        {books.map((book) => (
          <div
            key={book.id}
            className="book-card"
            onClick={() => setSelectedBook(book)}
          >
            <img src={book.image_url} alt={book.title} />
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <p>Available: {book.available_copies}</p>
          </div>
        ))}
      </div>

      {/* ðŸ”¥ Book details shown on SAME PAGE */}
      {selectedBook && (
        <BookDetailsModal
          bookId={selectedBook.id}
          onClose={() => setSelectedBook(null)}
        />
      )}
    </div>
  );
};

export default CategoryBooks;
