// src/components/books/FeaturedRentals.js
import React from "react";
import BookCard from "./BookCard";

const featuredBooks = [
  { type: "Book", title: "The Midnight Library", author: "Matt Haig", price: 2.99, duration: "7 days", rating: 4.5 },
  { type: "Audiobook", title: "Atomic Habits", author: "James Clear", price: 3.99, duration: "14 days", rating: 4.8 },
  { type: "Magazine", title: "National Geographic", author: "NG Society", price: 1.99, duration: "30 days", rating: 4.3 },
  { type: "Comic", title: "Spider-Man: Origins", author: "Marvel", price: 1.49, duration: "7 days", rating: 4.7 },
];

const FeaturedRentals = () => {
  return (
    <div className="container">
      <h2 className="section-title">Featured Rentals</h2>
      <div className="media-grid">
        {featuredBooks.map((book, index) => (
          <BookCard key={index} book={book} />
        ))}
      </div>
    </div>
  );
};

export default FeaturedRentals;
