// src/components/rentals/RentModal.jsx
import React, { useState } from "react";
import { rentBook } from "../../services/rentalService";

function RentModal({ book, onClose, onRentalSuccess }) {
  const [duration, setDuration] = useState(14); // default 2 weeks
  const [loading, setLoading] = useState(false);

  const handlePayAndRent = async () => {
    setLoading(true);
    try {
      // 1. Simulate payment (dummy)
      await new Promise((resolve) => setTimeout(resolve, 1000)); // 1 second

      // 2. Call backend to create rental
      await rentBook(book.id, duration);

      alert("Payment successful! Book rented.");

      // 3. Notify parent component to refresh rentals
      onRentalSuccess && onRentalSuccess();

      // 4. Close modal
      onClose();
    } catch (err) {
      alert(`Failed to rent book: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-backdrop">
      <div className="modal-content">
        <h2>Rent "{book.title}"</h2>
        <p>Author: {book.author}</p>
        <p>Available Copies: {book.available_copies}</p>
        <p>Description: {book.description}</p>

        <label>
          Duration (days):
          <input
            type="number"
            min="1"
            max="30"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
        </label>

        <div className="modal-buttons">
          <button onClick={onClose} disabled={loading}>
            Cancel
          </button>
          <button onClick={handlePayAndRent} disabled={loading}>
            {loading ? "Processing..." : "Pay & Rent"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default RentModal;
