// src/components/books/Categories.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch categories from backend
    axios
      .get("http://localhost:5000/api/books") // Adjust URL if needed
      .then((res) => {
        // Extract unique genres as categories
        const books = res.data.books || [];
        const genreMap = {};

        books.forEach((book) => {
          if (book.genre) {
            if (!genreMap[book.genre]) genreMap[book.genre] = 0;
            genreMap[book.genre] += 1;
          }
        });

        const categoryList = Object.entries(genreMap).map(([title, items]) => ({
          title,
          items,
        }));

        setCategories(categoryList);
      })
      .catch((err) => console.error("Error fetching categories:", err));
  }, []);

  const handleClick = (category) => {
    // Navigate to a category-specific page, e.g., /books/category/Programming
    navigate(`/books/category/${encodeURIComponent(category)}`);
  };

  return (
    <div className="container">
      <h2 className="section-title">Browse by Category</h2>
      <div className="category-grid">
        {categories.map((cat, index) => (
          <div
            key={index}
            className="category-card"
            onClick={() => handleClick(cat.title)}
            style={{ cursor: "pointer" }}
          >
            <div className="category-icon"></div>
            <h3>{cat.title}</h3>
            <p>{cat.items} items</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Categories;
